CREATE TABLE IF NOT EXISTS `system` (overview TEXT NOT NULL) DEFAULT CHARACTER SET utf8mb4;
CREATE TABLE IF NOT EXISTS contests (id VARCHAR(64) PRIMARY KEY, title TINYTEXT NOT NULL, `description` TEXT NOT NULL, explanation TEXT NOT NULL) DEFAULT CHARACTER SET utf8mb4;
CREATE TABLE IF NOT EXISTS tasks (contest VARCHAR(64) NOT NULL, task TINYINT NOT NULL, title TINYTEXT NOT NULL, problem TEXT NOT NULL, time_limit INT NOT NULL, accuracy TINYINT NOT NULL, testcases JSON NOT NULL, PRIMARY KEY(contest, task)) DEFAULT CHARACTER SET utf8mb4;
CREATE TABLE IF NOT EXISTS submissions (id VARCHAR(64) PRIMARY KEY, contest VARCHAR(64) NOT NULL, task TINYINT NOT NULL, lang VARCHAR(32) NOT NULL, code TEXT NOT NULL, whole_result TINYINT NOT NULL, max_memory INT NOT NULL, max_time INT NOT NULL, compile_error TEXT NOT NULl, details JSON NOT NULL) DEFAULT CHARACTER SET utf8mb4;
